initialDsl:
architecture "Shop" {
  system WebShop {
    container WebApp "Frontend"
    container API "Backend API"
    datastore DB "PostgreSQL"
  }
  WebApp -> API "Calls"
  API -> DB "Reads/Writes"
  // TODO: Add a component named Auth inside API
}

solution:
architecture "Shop" {
  system WebShop {
    container WebApp "Frontend"
    container API "Backend API" {
      component Auth "Authentication"
    }
    datastore DB "PostgreSQL"
  }
  WebApp -> API "Calls"
  API -> DB "Reads/Writes"
}
