solution:
architecture "Shop" {
  person Customer "Online Shopper"

  system WebShop {
    container WebApp "Web Application"
    container API "API Service"
    datastore DB "PostgreSQL Database"
  }

  Customer -> WebApp "Uses"
  WebApp -> API "Calls"
  API -> DB "Reads/Writes"
}
