svg.script:

let currentView = 'level1';
let selectedElement = null;
let scale = 1;
let panX = 0;
let panY = 0;
let isDragging = false;
let startX = 0;
let startY = 0;

function loadContent(dataId) {
    const sourceGroup = document.getElementById(dataId);
    const injectionArea = document.getElementById('contentInjection');
    const initialText = document.getElementById('initialText');
    if (!sourceGroup) { console.error('Content ID not found: ' + dataId); return; }
    initialText.style.display = 'none';
    injectionArea.innerHTML = '';
    let yOffset = 0;
    Array.from(sourceGroup.children).forEach(child => {
        const clonedChild = child.cloneNode(true);
        clonedChild.setAttribute('y', yOffset);
        if (clonedChild.classList.contains('section-header')) { yOffset += 50; }
        else if (clonedChild.classList.contains('doc-list-item')) { yOffset += 28; }
        else { yOffset += 35; }
        injectionArea.appendChild(clonedChild);
    });
}

function switchView(viewId) {
    // Hide all views
    document.querySelectorAll('#diagramArea > g').forEach(view => {
        view.style.display = 'none';
    });
    // Show the requested view
    const targetView = document.getElementById(viewId === 'level1' ? 'level1' : 'view-' + viewId);
    if (targetView) {
        targetView.style.display = 'block';
        currentView = viewId;
        // Get level from view element's data-level attribute
        const level = parseInt(targetView.getAttribute('data-level')) || 1;
        // Update level button states
        document.querySelectorAll('[data-level]').forEach(btn => {
            const btnLevel = parseInt(btn.getAttribute('data-level'));
            if (btnLevel === level) {
                btn.classList.add('button-active');
                const textEl = btn.querySelector('.button-text');
                if (textEl) textEl.classList.add('button-text-active');
            } else {
                btn.classList.remove('button-active');
                const textEl = btn.querySelector('.button-text');
                if (textEl) textEl.classList.remove('button-text-active');
            }
        });
    } else {
        console.error('View not found: ' + viewId);
    }
    clearSelection();
    resetZoom();
}

function switchLevel(level) {
    if (level === 1) {
        switchView('level1');
    } else {
        // For level 2/3, show the first available view of that level
        const views = document.querySelectorAll('#diagramArea > g[data-level="' + level + '"]');
        if (views.length > 0) {
            const firstView = views[0];
            const viewId = firstView.id === 'level1' ? 'level1' : firstView.id.replace('view-', '');
            switchView(viewId);
        } else {
            // Fallback to level 1 if no view found for requested level
            switchView('level1');
        }
    }
}

function drillDown(elementId) {
    // Check if there's a view for this element
    const viewEl = document.getElementById('view-' + elementId);
    if (viewEl) {
        switchView(elementId);
    } else {
        console.log('No drill-down view available for: ' + elementId);
    }
}

function selectElement(element) {
    clearSelection();
    selectedElement = element;
    element.classList.add('selected');
}

function clearSelection() {
    if (selectedElement) { selectedElement.classList.remove('selected'); }
    selectedElement = null;
    document.querySelectorAll('.interactive').forEach(el => el.classList.remove('selected'));
}

// Zoom and Pan
function updateTransform() {
    const diagramArea = document.getElementById('diagramArea');
    diagramArea.setAttribute('transform', `translate(${panX}, ${panY}) scale(${scale})`);
}

function zoomIn() {
    scale *= 1.2;
    updateTransform();
}

function zoomOut() {
    scale /= 1.2;
    updateTransform();
}

function resetZoom() {
    scale = 1;
    panX = 0;
    panY = 0;
    updateTransform();
}

function startDrag(evt) {
    if (evt.target.classList.contains('interactive') || evt.target.closest('.interactive')) return;
    isDragging = true;
    startX = evt.clientX;
    startY = evt.clientY;
}

function drag(evt) {
    if (!isDragging) return;
    evt.preventDefault();
    const dx = evt.clientX - startX;
    const dy = evt.clientY - startY;
    panX += dx;
    panY += dy;
    startX = evt.clientX;
    startY = evt.clientY;
    updateTransform();
}

function endDrag() {
    isDragging = false;
}

// Search
function search(query) {
    if (!query) return;
    query = query.toLowerCase();
    const elements = document.querySelectorAll('.interactive');
    let found = false;
    elements.forEach(el => {
        const text = el.textContent.toLowerCase(); // Simple text content search
        // Better: search data-content-id or specific labels
        // For now, let's search the text inside the element group/rect
        // Actually, the rect doesn't have text inside it in DOM structure here, 
        // text is sibling. We need a better way to link text to rect for search.
        // But wait, the 'interactive' class is on the rect. The text is separate.
        // We should probably search the data store or the visible text.

        // Let's search visible text elements that are siblings of interactive rects?
        // Or just search all text elements and if match, highlight parent/sibling?

        // Simpler approach: Iterate all text elements in the current level.
        if (el.nextElementSibling && el.nextElementSibling.textContent.toLowerCase().includes(query)) {
             selectElement(el);
             found = true;
             // Load content if found
             const dataId = el.getAttribute('data-content-id');
             if(dataId) loadContent(dataId);
        }
    });
    if (!found) {
        alert('No element found matching: ' + query);
    }
}

function init() {
    document.querySelectorAll('[data-level]').forEach(btn => {
        btn.addEventListener('click', function() {
            switchLevel(parseInt(this.getAttribute('data-level')));
        });
    });
    document.querySelectorAll('.interactive').forEach(element => {
        const elementId = element.id;
        const dataId = element.getAttribute('data-content-id');
        const drillView = element.getAttribute('data-drill-view');
        if (elementId) {
            element.addEventListener('click', function(e) {
                e.stopPropagation();
                // If drill-down is available, do that first
                if (drillView) {
                    drillDown(drillView);
                } else if (document.getElementById('view-' + elementId)) {
                    drillDown(elementId);
                }
                // Also load content if available
                if (dataId) {
                    loadContent(dataId);
                }
                selectElement(this);
            });
        }
    });
    document.querySelectorAll('#quickAccess [data-content-id]').forEach(btn => {
        btn.addEventListener('click', function() {
            loadContent(this.getAttribute('data-content-id'));
            clearSelection();
        });
    });

    // Zoom/Pan Listeners
    document.addEventListener('mousedown', startDrag);
    document.addEventListener('mousemove', drag);
    document.addEventListener('mouseup', endDrag);
    document.addEventListener('mouseleave', endDrag);

    // Zoom Buttons (to be added in UI)
    const btnZoomIn = document.getElementById('btnZoomIn');
    if(btnZoomIn) btnZoomIn.addEventListener('click', zoomIn);

    const btnZoomOut = document.getElementById('btnZoomOut');
    if(btnZoomOut) btnZoomOut.addEventListener('click', zoomOut);

    const btnReset = document.getElementById('btnReset');
    if(btnReset) btnReset.addEventListener('click', resetZoom);

    // Search Input (to be added in UI)
    const searchInput = document.getElementById('searchInput');
    if(searchInput) {
        searchInput.addEventListener('keypress', function(e) {
            if (e.key === 'Enter') {
                search(this.value);
            }
        });
    }

    switchLevel(1);
}

if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', init);
} else { init(); }
